                        ,rot.SYSTEM_VALUE_TXT                                                                                                                                                                                                                                                                                                                                                                                         
                        ,Dsg.SYSTEM_VALUE_TXT                                                                                                                                                                                                                                                                                                                                                                                       
                        ,pro.SYSTEM_VALUE_TXT                                                                                                                                                                                                                                                                                                                                                                                        
                        ,imp.SYSTEM_VALUE_TXT                                                                                                                                                                                                                                                                                                                                                                                        
                        ,cfc.SYSTEM_VALUE_TXT                                                                                                                                                                                                                                                                                                                                                                                       
                        ,pri.SYSTEM_VALUE_TXT                                                                                                                                                                                                                                                                                                                                                                                        
