                        ed.ECI_DIST_ID in (                                                                                                                                                                                                                                                                                                                                                                                        
                            select distinct ECI_DIST_ID from(                                                                                                                                                                                                                                                                                                                                                                                    
                                select                                                                                                                                                                                                                                                                                                                                                                                
