                                    ,t.DEPARTMENT, t.SECTION                                                                                                                                                                                                                                                                                                                                                                            
                                    ,d.DESIGN_ID, r.ROUTING_ID                                                                                                                                                                                                                                                                                                                                                                            
                                    ,p.PROJECT_ID, i.IMPL_STAGE_ID                                                                                                                                                                                                                                                                                                                                                                            
                                    ,cf.CAR_FAMILY_ID, pri.PRIORITY                                                                                                                                                                                                                                                                                                                                                                            
                                    ,e.ECI_TYPE, e.ROUTING_STATUS                                                                                                                                                                                                                                                                                                                                                                            
